CREATE TABLE `bid_policylaw` (
  `id` bigint(20) NOT NULL COMMENT '主键',
  `title` varchar(100) NOT NULL DEFAULT '' COMMENT '标题',
  `content` longtext NOT NULL COMMENT '内容',
  `publish_datetime` datetime DEFAULT NULL COMMENT '发布时间',
